import React , {useState} from 'react'


export const Signup = () => {
    const [user , setUser ]= useState({
        name: "" , email: "" , phone: "" , work: "" , password: "" , cpassword: ""

    });


    let name , value;

    const handleInputs = (e)=>{
        console.log(e);
        name = e.target.name;
        value= e.target.value;

        setUser({...user , [name]:value});
    }
  return (
    <>
     <form action="/signup" method="post">
          
          <input type="text" placeholder='name' name="name"  value={user.name} onChange={handleInputs} required />
          <input type="text" placeholder='email' name="email"  value={user.email} onChange={handleInputs} required />
          <input type="text" placeholder='mobile' name="phone"  value={user.name} onChange={handleInputs} required />
          <input type="text" placeholder='work' name="work"  value={user.name} onChange={handleInputs} required />
          <input type="text" placeholder='password' name="passowrdk" value={user.name} onChange={handleInputs}  required />
          <input type="text" placeholder='cpassword' name="cpassword"  value={user.name} onChange={handleInputs} required />





     </form>
    
    
    </>
  )
}
