import React , {Component} from 'react'
import { NavLink } from "react-router-dom";


import "../../Styles/NavigationBar.css"

import logo from "../../images/logo.png";

class NavigationBar extends Component {
  state={clicked:false}

  handleClick = ()=>{
    this.setState({clicked: !this.state.clicked})
  }

render(){
  return (
    <>
      <nav id='Navigation' >
         <a to='index.html'>
            <img src={logo} alt="" />
         </a>
         <div>
            <ul id="Navigation-items" className={this.state.clicked ? '#Navigation-items active' : '#Navigation-items'}>
                <li><a className="active" to='/'> Home</a></li>
                <li><a to='/about'> About </a></li>
                <li><a to='/contact'> Contact</a></li>
                <li><a to='/signup'> Signup</a></li>
                <li><a to='/login'> Login</a></li>

            </ul>
         </div>


         <div id='Navigation-mobile' onClick={this.handleClick}>
            <i id="bar" className={this.state.clicked ?'fas fa-times' : "fas fa-bars"}></i>
            
         </div>


      </nav>
    
    
    </>
  )
}
}

export default NavigationBar;
