import React , {useState} from 'react'
import { NavLink , useHistory } from 'react-router-dom';


export const Signup = () => {
    const history = useHist
    const [user , setUser ]= useState({
        name: "" , email: "" , phone: "" , work: "" , password: "" , cpassword: ""

    });


    let name , value;

    const handleInputs = (e)=>{
        console.log(e);
        name = e.target.name;
        value= e.target.value;

        setUser({...user , [name]:value});
    }

    const PostData = async(e)=>{
     e.preventDefault();

     const { name , email , phone , work , password, cpassword} = user;

     const res = await fetch("/register" , {
        method: "POST",
        headers:{
            "Content-Type" : "application/json"

        },
        body:JSON.stringify({
            name , email , phone , work , password, cpassword

        })
     });

     const res2 = await res.json();
     if(res2.status === 422 || !data){
        window.alert("Invalid registration");
        console.log("invalid registration");

     }

     else{

        window.alert("registration succesfull");
        console.log(" registration successfull");
        history.push("/login")

     }


    }


  return (
    <>
     <form method="post">
          
          <input type="text" placeholder='name' name="name"  value={user.name} onChange={handleInputs} required />
          <input type="text" placeholder='email' name="email"  value={user.email} onChange={handleInputs} required />
          <input type="text" placeholder='mobile' name="phone"  value={user.phone} onChange={handleInputs} required />
          <input type="text" placeholder='work' name="work"  value={user.work} onChange={handleInputs} required />
          <input type="text" placeholder='password' name="password" value={user.password} onChange={handleInputs}  required />
          <input type="text" placeholder='cpassword' name="cpassword"  value={user.cpassword} onChange={handleInputs} required />
          <input type="submit" value="register" name="signup"
          onClick={{PostData}}
           />





     </form>
    
    
    </>
  )
}
